import React, {useEffect} from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import {useDispatch, useSelector} from 'react-redux';
import {fetchNewsById} from '../store/actions/index';
import {useParams} from 'react-router-dom'; /* if we want to use params passed in app.js or anywhere in react */


const ShowNews = () => {
  const newsItem = useSelector((store) => store.newsReducer.newsItem);
  console.log(newsItem);
  const dispatch = useDispatch();
  const params = useParams();

  useEffect(()=>{
    dispatch(fetchNewsById(params.id)) /* if we want to call any action we need to use dispatch */
  },[dispatch, params])

  return (
    <>
        <div className='article_container'>
          <h1>newsItem.title</h1>
          <div style={{backgroundImage:`url($())`}} className='image'></div>
          <div className='author'>
              <span>Created by: Admin</span>
              <div>Created at: 9:54 PM</div>
          </div>
          <div className='mt-3 content'>
            Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged.
          </div>
          <LinkContainer to={'news/:id/edit'}>
            <button className='btn btn-warning my-3 me-3'>Edit</button>
          </LinkContainer>
          <button className='btn btn-danger my-3'>Delete</button>
        </div>
    </>
  )
}

export default ShowNews
