import axios from 'axios';

const fetchNewsHelper = async () => {
    try {
        const res = await axios.get('http://localhost:3001/news')
        // console.log(res);
        return res.data;
    } catch (error) {
        console.log(error);
    }
}


export const fetchNews = () => {
    return ({
        type: 'fetch_news',
        payload: fetchNewsHelper()
    })
}
