import React, {useEffect} from 'react';
import Masonry from 'react-masonry-css';
import { LinkContainer } from 'react-router-bootstrap';
import {useDispatch, useSelector} from 'react-redux';
import {fetchNews} from '../store/actions/index'


const Home = () => {

  const allNews = useSelector((store) => store.newsReducer.allNews);
  console.log(allNews);
  const dispatch = useDispatch();

  useEffect(()=>{
    dispatch(fetchNews()) /* if we want to call any action we need to use dispatch */
  },[dispatch])

  const breakpointColumnsObj = {
    default: 4,
    1100: 3,
    700: 2,
    500: 1
  };
  return (
    <>
      <Masonry
          breakpointCols={breakpointColumnsObj}
          className="my-masonry-grid"
          columnClassName="my-masonry-grid_column">
            {allNews.map((ele, idx)=>{

              <div key={idx}>
                <img className='responsive-img' src= {ele.image} alt="" />
                <div className='author'>
                  <span>{ele.author}</span>
                </div>
                <div className='content'>
                  <div className='title'>Title</div>
                  <div className='excerpt'>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. </div>
                  <LinkContainer to={"/news/show"}>
                    <button className='btn btn-info mt-3'>Read More</button>
                  </LinkContainer>
                </div>
              </div>
            })}
      </Masonry>
    </>
  )
}

export default Home
